import React, { useState, useEffect } from 'react';
import { v4 as uuidv4 } from 'uuid';

function ClientePanel({ reservas, setReservas }) {
  const [cliente, setCliente] = useState('');
  const [fecha, setFecha] = useState('');
  const [hora, setHora] = useState('');
  const [mensaje, setMensaje] = useState('');
  const [mensajeTipo, setMensajeTipo] = useState(''); // 'error' o 'exito'

  const crearReserva = () => {
    if (!cliente || !fecha || !hora) {
      setMensajeTipo('error');
      setMensaje('Por favor, complete todos los campos.');
      return;
    }

    // Verificar si el horario ya está ocupado
    const horarioOcupado = reservas.some(
      (r) => r.fecha === fecha && r.hora === hora
    );

    if (horarioOcupado) {
      setMensajeTipo('error');
      setMensaje('Este horario ya está ocupado. Por favor elige otro.');
      return;
    }

    // Crear nueva reserva
    const nuevaReserva = {
      id: uuidv4(),
      cliente,
      fecha,
      hora,
    };
    setReservas([...reservas, nuevaReserva]);
    setCliente('');
    setFecha('');
    setHora('');
    setMensajeTipo('exito');
    setMensaje('Reserva creada exitosamente');
  };

  // Opcional: Limpiar mensaje después de 5 segundos
  useEffect(() => {
    if (mensaje) {
      const timer = setTimeout(() => {
        setMensaje('');
        setMensajeTipo('');
      }, 5000);
      return () => clearTimeout(timer);
    }
  }, [mensaje]);

  return (
    <div>
      <h2>Crear Reserva</h2>
      <input
        type="text"
        placeholder="Nombre del Cliente"
        value={cliente}
        onChange={(e) => setCliente(e.target.value)}
      />
      <input
        type="date"
        value={fecha}
        onChange={(e) => setFecha(e.target.value)}
      />
      <input
        type="time"
        value={hora}
        onChange={(e) => setHora(e.target.value)}
      />
      <button
        onClick={crearReserva}
        disabled={!cliente || !fecha || !hora}
        style={{
          cursor: !cliente || !fecha || !hora ? 'not-allowed' : 'pointer',
          backgroundColor: !cliente || !fecha || !hora ? '#ccc' : '#4CAF50',
          color: !cliente || !fecha || !hora ? '#666' : 'white',
          padding: '10px',
          border: 'none',
          borderRadius: '5px',
          fontSize: '16px',
        }}
      >
        Crear Reserva
      </button>
      {mensaje && (
        <p style={{ color: mensajeTipo === 'error' ? 'red' : 'green', marginTop: '10px' }}>
          {mensaje}
        </p>
      )}
    </div>
  );
}

export default ClientePanel;
