import React from 'react';

function RecepcionistaPanel({ reservas, setReservas }) {
  const confirmarReserva = (id) => {
    const nuevas = reservas.map((r) =>
      r.id === id ? { ...r, estado: 'Confirmada', mensaje: 'Tu reserva ha sido confirmada.' } : r
    );
    setReservas(nuevas);
  };

  const cancelarReserva = (id) => {
    const nuevas = reservas.map((r) =>
      r.id === id ? { ...r, estado: 'Cancelada', mensaje: 'Tu reserva ha sido cancelada por el recepcionista.' } : r
    );
    setReservas(nuevas);
  };

  return (
    <div>
      <h2>Gesti√≥n de Reservas</h2>
      <ul>
        {reservas.map((res) => (
          <li key={res.id}>
            Cliente: {res.cliente} | {res.fecha} {res.hora} | Estado: {res.estado}
            <button
              onClick={() => confirmarReserva(res.id)}
              disabled={res.estado !== 'Pendiente'}
              style={{ marginLeft: '10px', color: 'green' }}
            >
              Confirmar
            </button>
            <button
              onClick={() => cancelarReserva(res.id)}
              disabled={res.estado === 'Cancelada'}
              style={{ marginLeft: '10px', color: 'red' }}
            >
              Cancelar
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default RecepcionistaPanel;
