import React, { useState } from 'react';

function AdminDashboard({ reservas, setReservas }) {
  const [nuevoUsuario, setNuevoUsuario] = useState({ nombre: '', rol: '' });

  const [nuevoReporte, setNuevoReporte] = useState("");

  // Función para eliminar una reserva (por ejemplo)
  const eliminarReserva = (id) => {
    const reservasFiltradas = reservas.filter(reserva => reserva.id !== id);
    setReservas(reservasFiltradas);
  };

  // Generación de reportes (por ahora solo una lista simple)
  const generarReporte = () => {
    setNuevoReporte(`Total de reservas: ${reservas.length}`);
  };

  return (
    <div>
      <h2>Panel de Administrador: Gestión de Usuarios</h2>

      {/* Reporte */}
      <div>
        <h3>Generar Reporte</h3>
        <button onClick={generarReporte}>Generar Reporte de Reservas</button>
        {nuevoReporte && <p>{nuevoReporte}</p>}
      </div>

      <hr />

      {/* Listado de Reservas */}
      <div>
        <h3>Listado de Reservas</h3>
        <ul>
          {reservas.map(reserva => (
            <li key={reserva.id}>
              Cliente: {reserva.cliente} - Hora: {reserva.hora}
              <button onClick={() => eliminarReserva(reserva.id)} style={{ marginLeft: '10px', color: 'red' }}>
                Eliminar
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default AdminDashboard;
