import React, { useState, useEffect } from 'react';
import { v4 as uuidv4 } from 'uuid';

function ClientePanel({ reservas, setReservas }) {
  const [cliente, setCliente] = useState('');
  const [fecha, setFecha] = useState('');
  const [hora, setHora] = useState('');
  const [mensaje, setMensaje] = useState('');
  const [mensajeTipo, setMensajeTipo] = useState('');

  const crearReserva = () => {
    if (!cliente || !fecha || !hora) {
      setMensajeTipo('error');
      setMensaje('Por favor completa todos los campos.');
      return;
    }

    const horarioOcupado = reservas.some(
      (r) => r.fecha === fecha && r.hora === hora
    );

    if (horarioOcupado) {
      setMensajeTipo('error');
      setMensaje('Este horario ya está reservado. Intenta con otro.');
      return;
    }

    const nuevaReserva = {
      id: uuidv4(),
      cliente,
      fecha,
      hora,
      estado: 'Pendiente',
      mensaje: ''
    };

    setReservas([...reservas, nuevaReserva]);
    setCliente('');
    setFecha('');
    setHora('');
    setMensajeTipo('exito');
    setMensaje('Reserva creada exitosamente.');
  };

  const cancelarReserva = (id) => {
    const actualizadas = reservas.map((r) =>
      r.id === id
        ? { ...r, estado: 'Cancelada', mensaje: 'Cancelada por el cliente.' }
        : r
    );
    setReservas(actualizadas);
  };

  useEffect(() => {
    if (mensaje) {
      const timer = setTimeout(() => {
        setMensaje('');
        setMensajeTipo('');
      }, 5000);
      return () => clearTimeout(timer);
    }
  }, [mensaje]);

  const reservasDelCliente = reservas.filter((r) => r.cliente === cliente);

  return (
    <div>
      <h2>Crear una nueva reserva</h2>
      <input
        type="text"
        placeholder="Tu nombre"
        value={cliente}
        onChange={(e) => setCliente(e.target.value)}
      />
      <input
        type="date"
        value={fecha}
        onChange={(e) => setFecha(e.target.value)}
      />
      <input
        type="time"
        value={hora}
        onChange={(e) => setHora(e.target.value)}
      />
      <button
        onClick={crearReserva}
        disabled={!cliente || !fecha || !hora}
        style={{
          cursor: !cliente || !fecha || !hora ? 'not-allowed' : 'pointer',
          backgroundColor: !cliente || !fecha || !hora ? '#ccc' : '#4CAF50',
          color: 'white',
          padding: '8px 16px',
          border: 'none',
          borderRadius: '5px',
          marginTop: '10px'
        }}
      >
        Crear Reserva
      </button>

      {mensaje && (
        <p style={{ color: mensajeTipo === 'error' ? 'red' : 'green' }}>{mensaje}</p>
      )}

      {cliente && (
        <>
          <h3>Mis Reservas</h3>
          {reservasDelCliente.length === 0 ? (
            <p>No tienes reservas aún.</p>
          ) : (
            <ul>
              {reservasDelCliente.map((res) => (
                <li key={res.id} style={{ marginBottom: '10px' }}>
                  <strong>{res.fecha}</strong> a las <strong>{res.hora}</strong> — Estado: <strong>{res.estado}</strong>
                  {res.mensaje && <div><em>Mensaje: {res.mensaje}</em></div>}
                  {res.estado === 'Pendiente' && (
                    <button
                      onClick={() => cancelarReserva(res.id)}
                      style={{
                        marginLeft: '10px',
                        backgroundColor: 'red',
                        color: 'white',
                        border: 'none',
                        padding: '5px 10px',
                        borderRadius: '5px',
                        cursor: 'pointer'
                      }}
                    >
                      Cancelar
                    </button>
                  )}
                </li>
              ))}
            </ul>
          )}
        </>
      )}
    </div>
  );
}

export default ClientePanel;
