import React, { useState } from 'react';

function ClientePanel({ reservas, setReservas }) {
  const [nombre, setNombre] = useState('');
  const [hora, setHora] = useState('');

  const agregarReserva = () => {
    if (nombre.trim() === '' || hora.trim() === '') {
      alert('Por favor, completa todos los campos');
      return;
    }
    const nuevaReserva = {
      id: reservas.length + 1,
      cliente: nombre,
      hora: hora
    };
    setReservas([...reservas, nuevaReserva]);
    setNombre('');
    setHora('');
  };

  return (
    <div>
      <h2>Panel del Cliente: Visualizar y Crear Reservas</h2>
      <div>
        <input
          type="text"
          placeholder="Nombre del cliente"
          value={nombre}
          onChange={(e) => setNombre(e.target.value)}
        />
        <input
          type="time"
          placeholder="Hora de la reserva"
          value={hora}
          onChange={(e) => setHora(e.target.value)}
        />
        <button onClick={agregarReserva}>Crear Reserva</button>
      </div>
      <h3>Mis Reservas</h3>
      <ul>
        {reservas.map((reserva) => (
          <li key={reserva.id}>
            Cliente: {reserva.cliente} - Hora: {reserva.hora}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ClientePanel;
