import React, { useState } from 'react';

function Login({ login }) {
  const [rol, setRol] = useState('');

  const handleLogin = () => {
    if (rol === 'admin' || rol === 'recepcionista' || rol === 'cliente') {
      login(rol);
    } else {
      alert('Por favor, ingresa un rol válido (admin, recepcionista o cliente).');
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <input
        type="text"
        placeholder="Usuario (admin, recepcionista, cliente)"
        value={rol}
        onChange={(e) => setRol(e.target.value)}
      />
      <button onClick={handleLogin}>Ingresar</button>
    </div>
  );
}

export default Login;
