import React, { useState } from 'react';

function AdminDashboard() {
  // Estado para usuarios (solo en memoria por ahora)
  const [usuarios, setUsuarios] = useState([
    { id: 1, nombre: 'Carlos', rol: 'Admin' },
    { id: 2, nombre: 'Ana', rol: 'Recepcionista' },
    { id: 3, nombre: 'Luis', rol: 'Cliente' }
  ]);

  // Estado para nuevo usuario
  const [nuevoUsuario, setNuevoUsuario] = useState({ nombre: '', rol: '' });

  // Función para agregar un usuario
  const agregarUsuario = () => {
    if (nuevoUsuario.nombre && nuevoUsuario.rol) {
      const nuevoId = usuarios.length + 1;
      setUsuarios([...usuarios, { id: nuevoId, ...nuevoUsuario }]);
      setNuevoUsuario({ nombre: '', rol: '' }); // Limpiar los campos
    }
  };

  // Función para eliminar un usuario
  const eliminarUsuario = (id) => {
    const usuariosFiltrados = usuarios.filter(usuario => usuario.id !== id);
    setUsuarios(usuariosFiltrados);
  };

  return (
    <div>
      <h2>Panel de Administrador: Gestión de Usuarios</h2>

      {/* Formulario para agregar nuevo usuario */}
      <div>
        <h3>Agregar Usuario</h3>
        <input 
          type="text" 
          placeholder="Nombre del usuario" 
          value={nuevoUsuario.nombre} 
          onChange={e => setNuevoUsuario({ ...nuevoUsuario, nombre: e.target.value })}
        />
        <input 
          type="text" 
          placeholder="Rol del usuario" 
          value={nuevoUsuario.rol} 
          onChange={e => setNuevoUsuario({ ...nuevoUsuario, rol: e.target.value })}
        />
        <button onClick={agregarUsuario}>Agregar</button>
      </div>

      {/* Lista de usuarios */}
      <div>
        <h3>Usuarios Registrados</h3>
        <ul>
          {usuarios.map(usuario => (
            <li key={usuario.id}>
              {usuario.nombre} - {usuario.rol}
              <button onClick={() => eliminarUsuario(usuario.id)} style={{ marginLeft: '10px', color: 'red' }}>
                Eliminar
              </button>
            </li>
          ))}
        </ul>
      </div>

    </div>
  );
}

export default AdminDashboard;
